package com.example.nbateams

import androidx.lifecycle.ViewModel

class SharedViewModel : ViewModel() {

    var teamList : MutableList<Team> = mutableListOf()

   lateinit var itemSelected : Team


    init{
        teamList.add(Team("Los Angeles Lakers","Western","California", "Kobe Bryant", "data:image/png;base64,"))
        teamList.add(Team("Boston Celtics","Eastern","Massachusetts", "Bill Russel", "https://upload.wikimedia.org/wikipedia/en/thumb/8/8f/Boston_Celtics.svg/1200px-Boston_Celtics.svg.png"))
        teamList.add(Team("Chicago Bulls","Eastern","Illinois", "Michael Jordan", "https://logos-world.net/wp-content/uploads/2020/05/Chicago-Bulls-Symbol.png"))
        teamList.add(Team("Golden State Warriors","Western","California", "Stephen Curry", "https://assets.stickpng.com/images/58419ce2a6515b1e0ad75a69.png"))
        teamList.add(Team("Philadelphia 76ers","Eastern","Pennsylvania", "Julius Erving", "data:image/png;base64,"))
        teamList.add(Team("San Antonio Spurs","Western","Texas", "Tim Duncan", "data:image/png;base64,"))
        teamList.add(Team("Milwaukee Bucks","Eastern","Wisconsin", "Kareem Abdul-Jabbar", "https://assets.stickpng.com/images/58419ba7a6515b1e0ad75a54.png"))
        teamList.add(Team("Dallas Mavericks","Western","Texas", "Dirk Nowitzki", "https://logodownload.org/wp-content/uploads/2021/07/dallas-mavericks-logo-1.png"))
    }

    var userList : MutableList<User> = mutableListOf()

    fun getTeams(): MutableList<Team> {
        return teamList
    }

    fun getUsers(): MutableList<User> {
        return userList
    }

    fun checkCredentials(username: String, password: String): Boolean {
        return userList.any { it.username == username && it.pass == password }
    }

    fun checkExistence(newUsername: String): Boolean {
        return userList.none { it.username == newUsername }
    }


    fun addUser(username: String, password: String){
        val newUser = User(username, password)
        userList.add(newUser)
    }


}